mapper.select:
SELECT lin.line_name AS lineName, eq.equipment_name AS equipmentName, eq.equipment_no AS equipmentNo, ma.device_id as deviceId, ma.line_id as lineId, ma.date_of_production AS dateOfProduction, ma.maintain_date_before AS maintainDateBefore, ma.maintain_date_after AS maintainDateAfter, ma.multiple as multiple, ma.unit as unit, ma.frequency as frequency, ma.maintain_id AS maintainId FROM dz_maintain_device AS ma LEFT JOIN dz_production_line AS lin ON ma.line_id = lin.id LEFT JOIN dz_equipment AS eq ON ma.device_id = eq.id WHERE ma.org_code = #{useOrgCode}
AND ma.line_id = #{lineId}
AND eq.equipment_no = #{equipmentNo}
AND ma.maintain_date_after >= #{startTime}
AND ma.maintain_date_after <= #{endTime}

SELECT lin.line_name AS lineName, eq.equipment_name AS equipmentName, eq.equipment_no AS equipmentNo, ma.device_id as deviceId, ma.line_id as lineId, ma.date_of_production AS dateOfProduction, ma.maintain_date_before AS maintainDateBefore, ma.maintain_date_after AS maintainDateAfter, ma.multiple, ma.unit, ma.frequency, ma.maintain_id AS maintainId FROM dz_maintain_device AS ma LEFT JOIN dz_production_line AS lin ON ma.line_id = lin.id LEFT JOIN dz_equipment AS eq ON ma.device_id = eq.id WHERE ma.maintain_date_after > #{now}
AND ma.line_id = #{lineId}
AND eq.equipment_no = #{equipmentNo}
AND ma.maintain_date_after >= #{startTime}
AND ma.maintain_date_after <= #{endTime}

SELECT lin.line_name AS lineName, eq.equipment_name AS equipmentName, eq.equipment_no AS equipmentNo, ma.device_id as deviceId, ma.line_id as lineId, ma.date_of_production AS dateOfProduction, ma.maintain_date_before AS maintainDateBefore, ma.maintain_date_after AS maintainDateAfter, ma.multiple, ma.unit, ma.frequency, ma.maintain_id AS maintainId FROM dz_maintain_device AS ma LEFT JOIN dz_production_line AS lin ON ma.line_id = lin.id LEFT JOIN dz_equipment AS eq ON ma.device_id = eq.id WHERE ma.maintain_date_after < #{now}
AND ma.line_id = #{lineId}
AND eq.equipment_no = #{equipmentNo}
AND ma.maintain_date_after >= #{startTime}
AND ma.maintain_date_after <= #{endTime}

SELECT ma.maintain_history_id AS maintainHistoryId, ma.maintain_date AS maintainDate, lin.line_name AS lineName, eq.equipment_name AS equipmentName, eq.equipment_no AS equipmentNo, ma.create_by AS createBy FROM dz_maintain_device_history AS ma LEFT JOIN dz_maintain_device AS de ON ma.maintain_id = de.maintain_id LEFT JOIN dz_production_line AS lin ON de.line_id = lin.id LEFT JOIN dz_equipment AS eq ON de.device_id = eq.id WHERE ma.maintain_id = #{maintainId}
AND ma.create_by = #{createBy}
AND ma.maintain_date >= #{startTime}
AND ma.maintain_date <= #{endTime}
