mapper.select:
SELECT ( SELECT dep.depart_name FROM sys_depart AS dep WHERE dep.id = ( SELECT ord.depart_id FROM dz_order AS ord WHERE ord.order_no = da.order_no ) ) AS departName, ( SELECT pro.product_name FROM dz_product AS pro WHERE pro.product_no = da.product_no ) AS productName, da.order_no AS orderNo, da.detector_time as detectorTime, da.product_no as productNo, da.group_key FROM `dz_detector_data` AS da WHERE group_key in
#{id}
GROUP BY group_key

select
${detectionName}
from dz_workpiece_data
where
order_no = #{orderNo}
AND line_no =#{lineNo}
AND product_no=#{productNo}

    and check_date >=#{startTime}


    and check_date <=#{endTime}

order by detector_time asc
LIMIT 200;

select ddate.data_val as dataVal
from dz_detector_data ddate
             LEFT JOIN dz_product_detection_template dtem on ddate.detection_id = dtem.detection_id
WHERE ddate.product_no = #{productNo}
  AND ddate.order_no = #{orderNo}
order by ddate.detector_time desc

SELECT group_key, detector_time FROM ( SELECT group_key, detector_time FROM dz_detector_data WHERE product_no = #{productNo} AND org_code = #{orgCode}
AND all_state_use = #{detectionResult}
AND detector_time >= #{startTime}
AND detector_time < #{endTime}
GROUP BY group_key ) AS temp ORDER BY detector_time DESC
