mapper.update:
UPDATE dz_product_detection_template
set product_no=#{newProductNo}
where product_no = #{oldProductNo}
  and del_flag = 0

mapper.select:
select DISTINCT(productNo), departId, productName, lineName, lineId, orderId, orderNO, lineNo from ( SELECT tem.product_no AS productNo, tem.depart_id AS departId, pro.product_name AS productName, line.line_name as lineName, tem.line_id as lineId, tem.order_id as orderId, tem.order_no as orderNO, tem.line_no as lineNo FROM dz_product_detection_template AS tem LEFT JOIN dz_product AS pro ON pro.product_no = tem.product_no LEFT JOIN dz_production_line AS line ON line.id = tem.line_id WHERE tem.del_flag = 0
AND tem.order_id = #{orderId}
AND tem.line_id = #{lineId}
AND tem.depart_id = #{departId}
AND pro.product_name LIKE concat(#{productName,jdbcType=VARCHAR},'%')
) as temp
ORDER BY temp.${field} ${type}

SELECT table_col_con as colName,
       table_col_val as colData,
       is_show       as exhibition
FROM dz_product_detection_template
WHERE order_no = #{orderNo}
  AND line_no = #{lineNo}
  AND product_no = #{productNo}
  ORDER BY table_col_val;

SELECT compensation_value AS compensationValue,
       detection_id       AS detectionId,
       table_col_val      AS tableColVal,
       table_col_con      AS tableColCon,
       group_Id           as groupId
FROM dz_product_detection_template
WHERE order_id = #{orderId}
  and line_id = #{lineId}
  and product_no = #{productNo}

SELECT table_col_val,
       table_col_con
FROM dz_product_detection_template
WHERE product_no = #{productNo}
  AND is_show = #{show}
  AND line_id = #{lineId}
  AND order_id = #{orderId}
