mapper.select:
SELECT rep.repair_id AS repairId, rep.check_number AS checkNumber, rep.remarks, rep.start_handle_date AS startHandleDate, rep.complete_handle_date AS completeHandleDate, rep.fault_type AS faultType, rep.create_by AS createBy, rep.line_id AS lineId, lin.line_name AS lineName, eq.equipment_name AS equipmentName, eq.equipment_no AS equipmentNo, eq.id AS deviceId FROM dz_repair_history AS rep LEFT JOIN dz_production_line AS lin ON rep.line_id = lin.id LEFT JOIN dz_equipment AS eq ON rep.dievice_id = eq.id WHERE rep.org_code = #{useOrgCode}
AND rep.check_number = #{checkNumber}
AND rep.line_id = #{lineId}
AND rep.fault_type = #{faultType}
AND eq.equipment_no = #{equipmentNo}
AND rep.create_time >= #{startTime}
AND rep.create_time <= #{endTime}
ORDER BY temp.$ { field } $ { type }
